# coding ninjas - 3 Sum

def findTriplets(arr,s):

    #method 1 - for loop
    # ans = []
    # n = len(arr)
    # for i in range(n):
    #     for j in range(i+1, n):
    #         for k in range(j+1, n):
    #             if arr[i] + arr[j] + arr[k] == s:
    #                 triplet = [arr[i], arr[j], arr[k]]
    #                 if triplet not in ans:
    #                     ans.append(triplet)

    # method 2 - 3 pointer
    n = len(arr)
    ans = []
    unique_triplet = {}
    s_arr = sorted(arr)
    for i in range(n-2):
        j = i+1
        k = n-1
        while(j<k):
            total_ = s_arr[i] + s_arr[j] + s_arr[k]
            if total_ == s:
                triplet = sorted([s_arr[i], s_arr[j], s_arr[k]])
                temp = ':'.join(map(str, triplet))
                unique_triplet[temp] = i
                x = s_arr[j]
                y = s_arr[k]
                while(j<k and s_arr[j] == x):
                    j += 1
                while(j<k and s_arr[k] == y):
                    k -= 1
            
            elif total_ < s:
                j += 1

            elif total_ > s:
                k -= 1
        if (i+1<n and s_arr[i] == s_arr[i+1]):
            i += 1
            
    for val in unique_triplet.keys():
        ans.append(list(map(int, val.split(':'))))

    return ans

